import { ICSSRules } from "@microsoft/fast-jss-manager";
import { Direction, ellipsis, localizeSpacing, toPx } from "@microsoft/fast-jss-utilities";

/**
 * Base64 encoded svgs
 */
/* tslint:disable */
export const rightArrow: string = "url(data:image/svg+xml;base64,) center no-repeat";
export const lines: string = "url(data:image/svg+xml;base64,PHN2ZyB3aWR0aD0iMTYiIGhlaWdodD0iOSIgdmlld0JveD0iMCAwIDE2IDkiIGZpbGw9Im5vbmUiIHhtbG5zPSJodHRwOi8vd3d3LnczLm9yZy8yMDAwL3N2ZyI+PHBhdGggZD0iTTE2IDVIMFY0SDE2VjVaTTE2IDEzSDBWMTJIMTZWMTNaTTE2IDguOTkyMTlIMFY4SDE2VjguOTkyMTlaIiB0cmFuc2Zvcm09InRyYW5zbGF0ZSgwIC00LjAwMDAzKSIgZmlsbD0iYmxhY2siLz48L3N2Zz4=) center no-repeat";
export const plus: string = "url(data:image/svg+xml;base64,) center no-repeat";
export const pinkPlus: string = "url(data:image/svg+xml;base64,) center no-repeat";
export const minus: string = "url(data:image/svg+xml;base64,) center no-repeat";
export const lightTheme: string = "url(data:image/svg+xml;base64,) center no-repeat";
export const darkTheme: string = "url(data:image/svg+xml;base64,) center no-repeat";
export const trashcan: string = "url(data:image/svg+xml;base64,) center no-repeat";
/* tslint:enable */

export interface IBoxShadowConfig {
    offsetX: number;
    offsetY: number;
    blurRadius: number;
    spreadRadius: number;
    color: string;
    inset?: boolean;
}

export const colors: any = {
    black: "#000",
    white: "#FFF",
    blue: "#0078D4",
    pink: "#FB356D",
    lightPink: "#FB4E7F",
    darkPink: "#FB1C5B",
    gray: "#8A8A8A",
    grayBackground: "rgba(0, 0, 0, 0.04)",
    containerBackground: "rgb(244, 245, 246)",
    boxShadow: "rgba(0, 0, 0, 0.08)",
    hover: "rgba(0,0,0, .3)",
    border: "rgba(0,0,0, .2)",
    lightBorder: "rgba(0,0,0, .1)",
    menuGray: "#EBEBEB"
};

export function localizePadding(top: number, right: number, bottom: number, left: number): ICSSRules<{}> {
    return {
        padding: localizeSpacing(Direction.ltr)(`${toPx(top)} ${toPx(right)} ${toPx(bottom)} ${toPx(left)}`)
    };
}

export function boxShadow(config: IBoxShadowConfig): ICSSRules<{}> {
    return {
        boxShadow: `${config.inset ? "inset " : ""}
        ${toPx(config.offsetX)} ${toPx(config.offsetY)} ${toPx(config.blurRadius)} ${toPx(config.spreadRadius)} ${config.color}`
    };
}

/**
 * Mimics a border but with boxShadow (strong in the sense of no blur)
 */
export function insetStrongBoxShadow(color: string): ICSSRules<{}> {
    const shadow: IBoxShadowConfig = {
        offsetX: 0,
        offsetY: 0,
        blurRadius: 0,
        spreadRadius: 1,
        color,
        inset: true
    };

    return {
        ...boxShadow(shadow)
    };
}

export function insetHoverBoxShadow(): ICSSRules<{}> {
    const shadow: IBoxShadowConfig = {
        offsetX: 0,
        offsetY: 0,
        blurRadius: 2,
        spreadRadius: 0,
        color: colors.hover,
        inset: true
    };

    return {
        ...boxShadow(shadow)
    };
}

export function applyLabelStyle(): ICSSRules<{}> {
    return {
        flexGrow: "1",
        lineHeight: toPx(16),
        fontSize: toPx(14),
        marginRight: toPx(16),
        ...ellipsis()
    };
}

export function applyInputContainerStyle(): ICSSRules<{}> {
    return {
        height: toPx(36),
    };
}

export function applyInputStyle(): ICSSRules<{}> {
    const shadow: IBoxShadowConfig = {
        offsetX: 0,
        offsetY: 0,
        blurRadius: 4,
        spreadRadius: 0,
        color: colors.boxShadow,
        inset: true
    };

    return {
        lineHeight: toPx(16),
        fontSize: toPx(14),
        backgroundColor: colors.grayBackground,
        borderRadius: toPx(2),
        ...boxShadow(shadow),
        padding: `${toPx(10)} ${toPx(8)} ${toPx(10)}`,
        border: "none",
        outline: "none",
        "&:hover": {
            ...insetHoverBoxShadow()
        },
        "&:focus": {
            ...insetStrongBoxShadow(colors.pink)
        }
    };
}

export function applyWrapperStyle(): ICSSRules<{}> {
    return {
        display: "flex",
        flexDirection: "row",
        minHeight: toPx(40),
        alignItems: "center"
    };
}

/**
 * Common wrapper that surrounds a label and an input
 */
export function applyCleanListStyle(): ICSSRules<{}> {
    return {
        listStyle: "none",
        margin: "0",
        padding: "0",
        listStylePosition: "outside"
    };
}

export function applyHeaderStyle(): ICSSRules<{}> {
    return {
        display: "flex",
        alignItems: "center",
        margin: `${toPx(20)} 0 0 0`,
        minHeight: toPx(40),
        "& h3": {
            margin: toPx(0)
        },
        "& button": {
            marginTop: toPx(7)
        }
    };
}

export function applyGlobalStyle(): ICSSRules<{}> {
    return {
        "body > li[draggable=\"true\"]": {
            ...listItem,
            ...draggingStyles
        }
    };
}

export function applyListItemStyle(): ICSSRules<{}> {
    return {
        "& li": listItem
    };
}

export const draggingStyles: ICSSRules<{}> = {
    boxShadow: `0 ${toPx(4)} ${toPx(4)} ${toPx(-4)} rgba(0, 0, 0, 0.15)`,
    borderColor: "transparent",
    display: "flex",
    "&::after": {
        background: rightArrow,
        right: toPx(0)
    },
    "&::before": {
        background: lines,
        left: toPx(0)
    }
};

export const listItem: ICSSRules<{}> = {
    flex: "1 100%",
    borderBottom: `${toPx(1)} solid ${colors.border}`,
    alignItems: "center",
    position: "relative",
    cursor: "pointer",
    display: "flex",
    height: toPx(40),
    background: colors.containerBackground,
    "&::after, &::before": {
        position: "absolute",
        content: "''",
        opacity: ".6",
        pointerEvents: "none",
        top: toPx(13),
        width: toPx(16),
        height: toPx(16)
    },
    "&::after": {
        background: rightArrow,
        right: "0"
    },
    "&::before": {
        background: lines,
        left: "0"
    },
    "& button": {
        fontSize: toPx(14),
        border: "none",
        background: "transparent",
        textAlign: "left",
        marginLeft: toPx(25),
        ...localizePadding(12, 18, 12, 1)
    },
    "& a": {
        textAlign: "left",
        display: "block",
        minHeight: toPx(19),
        ...localizePadding(0, 0, 0, 26),
        "& span": {
            display: "block",
            fontStyle: "italic",
            fontSize: toPx(13),
            paddingTop: toPx(4)
        }
    }
};

export function applyAriaHiddenStyles(): ICSSRules<{}> {
    return {
        "&[aria-hidden=\"true\"]": {
            display: "none"
        },
        "&[aria-hidden=\"false\"]": {
            display: "block"
        }
    };
}

export function applyPopupMenuStyles(): ICSSRules<{}> {
    const shadow: IBoxShadowConfig = {
        offsetX: 0,
        offsetY: 1,
        blurRadius: 0,
        spreadRadius: 0,
        color: colors.border
    };

    return {
        position: "absolute",
        right: "0",
        width: toPx(200),
        background: colors.menuGray,
        zIndex: "1",
        top: toPx(55),
        ...boxShadow(shadow),
        "& li": {
            "& > button": {
                cursor: "pointer",
                display: "block",
                ...localizePadding(12, 12, 12, 36),
                position: "relative",
                border: "none",
                background: "transparent",
                textAlign: "left",
                width: "100%",
                ...ellipsis(),
                lineHeight: toPx(16),
                "&:focus": {
                    outline: "none"
                },
                "&:hover": {
                    backgroundColor: colors.grayBackground
                }
            },
            "& > span": {
                display: "block",
                padding: toPx(12)
            }
        }
    };
}

/**
 * As opposed to text-decoration: underline
 */
export const thickLine: ICSSRules<{}> = {
    "& > span": {
        position: "relative",
        paddingTop: toPx(3),
        paddingBottom: toPx(3),
        display: "block",
        ...ellipsis(),
    },
    "&:hover, &:focus": {
        outline: "none",
        "& > span": {
            "&::after": {
                position: "absolute",
                content: "''",
                pointerEvents: "none",
                display: "block",
                background: colors.pink,
                width: "100%",
                height: toPx(2),
                bottom: "0"
            }
        }
    }
};

export function applyPopupHeadingStyles(): ICSSRules<{}> {
    return {
        position: "relative",
        "& > div": {
            display: "flex",
            borderBottom: `${toPx(1)} solid ${colors.border}`,
            "& h3": {
                flexGrow: "1",
                "& + button": {
                    fontSize: toPx(14),
                    background: "none",
                    border: "none",
                    padding: `${toPx(2)} ${toPx(12)}`,
                    color: colors.pink,
                    ...thickLine,
                    "&[aria-expanded=\"true\"]": {
                        background: colors.menuGray,
                        borderLeftColor: colors.lightBorder,
                        borderRadius: `${toPx(2)} ${toPx(2)} 0 0`,
                        borderRightColor: colors.lightBorder,
                        borderTopColor: colors.lightBorder,
                        color: colors.black,
                        "&:hover, &:focus": {
                            "& > span::after": {
                                display: "none"
                            }
                        },
                        "& > span::after": {
                            display: "none"
                        }
                    }
                }
            }
        }
    };
}

/**
 * Used for styles radio buttons (vertical and horizontal alignment)
 */
export function applyInputBackplateStyle(): ICSSRules<{}> {
    const shadow: IBoxShadowConfig = {
        offsetX: 0,
        offsetY: 0,
        blurRadius: 0,
        spreadRadius: 2,
        color: colors.black,
        inset: true
    };

    return {
        appearance: "none",
        height: toPx(36),
        width: toPx(36),
        margin: toPx(0),
        borderRadius: toPx(2),
        backgroundColor: "transparent",
        "&:focus, &:hover": {
            outline: "none",
            ...boxShadow(shadow)
        }
    };
}

export function applySelectInputStyles(): ICSSRules<{}> {
    const shadow: IBoxShadowConfig = {
        offsetX: 0,
        offsetY: 0,
        blurRadius: 4,
        spreadRadius: 0,
        color: colors.boxShadow,
        inset: true
    };

    return {
        width: "100%",
        lineHeight: toPx(16),
        fontSize: toPx(14),
        backgroundColor: colors.grayBackground,
        borderRadius: toPx(2),
        ...boxShadow(shadow),
        appearance: "none",
        ...localizePadding(10, 36, 10, 10),
        border: "none",
        outline: "none",
        "&:-ms-expand": {
            display: "none"
        },
        "&:hover": {
            ...insetHoverBoxShadow()
        },
        "&:focus": {
            ...insetStrongBoxShadow(colors.pink)
        }
    };
}

export function applySelectSpanStyles(): ICSSRules<{}> {
    return {
        position: "relative",
        display: "flex",
        "&::before, &::after": {
            content: "''",
            position: "absolute",
            top: toPx(12),
            zIndex: "1",
            borderRadius: toPx(2),
            width: toPx(1),
            height: toPx(10),
            background: colors.black
        },
        "&::before": {
            right: toPx(15),
            transform: "rotate(45deg)"
        },
        "&::after": {
            right: toPx(22),
            transform: "rotate(-45deg)"
        }
    };
}

export function applyAddItemStyle(): ICSSRules<{}> {
    return {
        position: "relative",
        color: colors.pink,
        cursor: "pointer",
        border: "none",
        background: "transparent",
        lineHeight: toPx(16),
        fontSize: toPx(14),
        maxWidth: toPx(290),
        minHeight: toPx(40),
        "&::before": {
            position: "absolute",
            content: "''",
            pointerEvents: "none",
            background: pinkPlus,
            width: toPx(16),
            height: toPx(16),
            left: "0",
            top: "50%",
            transform: "translateY(-50%)"
        }
    };
}
